import React, { useState, useEffect, useRef } from 'react';
import VideoPlayer from './components/VideoPlayer';
import './App.css';

interface Subtitle {
  text: string;
  startTime: number;
  endTime: number;
}

interface TextBox {
  text: string;
  confidence: number;
  bbox: {
    x1: number;
    y1: number;
    x2: number;
    y2: number;
  };
}

interface MediaItem {
  id: string;
  type: 'video' | 'image';
  url: string;
  file: File;
  textBoxes?: TextBox[];
  subtitles?: Subtitle[];
}

interface CharBox {
  x1: number;
  y1: number;
  x2: number;
  y2: number;
}

interface DetectedObject {
  text: string;
  bbox: CharBox;
  char_boxes?: CharBox[];
}

const App: React.FC = () => {
  const [mediaItems, setMediaItems] = useState<MediaItem[]>([]);
  const [selectedMedia, setSelectedMedia] = useState<MediaItem | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<Subtitle[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const imageRef = useRef<HTMLImageElement>(null);
  const [detectedObjects, setDetectedObjects] = useState<DetectedObject[]>([]);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setIsProcessing(true);
      setError(null);
      const url = URL.createObjectURL(file);
      const type = file.type.startsWith('video/') ? 'video' : 'image';

      const formData = new FormData();
      formData.append(type, file);

      try {
        const response = await fetch(`http://localhost:5001/upload${type === 'image' ? '-image' : ''}`, {
          method: 'POST',
          body: formData,
        });

        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.error || 'Upload failed');
        }

        const newMediaItem: MediaItem = {
          id: Date.now().toString(),
          type,
          url,
          file,
          textBoxes: data.textBoxes,
          subtitles: data.subtitles
        };

        setMediaItems(prev => [...prev, newMediaItem]);
        setSelectedMedia(newMediaItem);
      } catch (error) {
        console.error('Error uploading file:', error);
        setError(error instanceof Error ? error.message : 'Error uploading file. Please try again.');
      } finally {
        setIsProcessing(false);
      }
    }
  };

  const handleSearch = async (mode: 'normal' | 'smart') => {
    console.log('검색 시작:', { searchTerm, mode });  // 디버깅용 로그 추가

    if (!searchTerm || searchTerm.trim() === '' || !selectedMedia) {
      console.log('검색어가 비어있거나 선택된 미디어가 없음');
      setDetectedObjects([]);
      return;
    }

    try {
      const formData = new FormData();
      formData.append('image', selectedMedia.file);
      formData.append('query', searchTerm);
      formData.append('mode', mode);

      console.log('API 요청 전송');  // 디버깅용 로그 추가
      const response = await fetch('http://localhost:5001/analyze-image', {
        method: 'POST',
        body: formData,
      });

      console.log('API 응답 받음:', response.status);  // 디버깅용 로그 추가

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || '검색 중 오류가 발생했습니다');
      }

      const data = await response.json();
      console.log('검색 결과:', data);  // 디버깅용 로그 추가

      if (data.objects && Array.isArray(data.objects)) {
        setDetectedObjects(data.objects);
      } else {
        console.log('검색 결과가 올바른 형식이 아님');
        setDetectedObjects([]);
      }
    } catch (error) {
      console.error('검색 오류:', error);
      setError(error instanceof Error ? error.message : '검색 중 오류가 발생했습니다');
      setDetectedObjects([]);
    }
  };

  const handleMediaClick = (media: MediaItem) => {
    setSelectedMedia(media);
    setSearchTerm('');
    setSearchResults([]);
  };

  const handleTimestampClick = (timestamp: number) => {
    const video = document.querySelector('video');
    if (video) {
      video.currentTime = timestamp;
      video.play();
    }
  };

  const handleImageClick = (event: React.MouseEvent<HTMLImageElement>) => {
    if (!imageRef.current || !selectedMedia?.textBoxes) return;

    const rect = imageRef.current.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    const clickedTextBox = selectedMedia.textBoxes.find(box => {
      const scaleX = rect.width / imageRef.current!.naturalWidth;
      const scaleY = rect.height / imageRef.current!.naturalHeight;

      const scaledX1 = box.bbox.x1 * scaleX;
      const scaledY1 = box.bbox.y1 * scaleY;
      const scaledX2 = box.bbox.x2 * scaleX;
      const scaledY2 = box.bbox.y2 * scaleY;

      return x >= scaledX1 && x <= scaledX2 && y >= scaledY1 && y <= scaledY2;
    });

    if (clickedTextBox) {
      setSearchTerm(clickedTextBox.text);
    }
  };

  // 검색 결과 초기화 함수
  const clearSearchResults = () => {
    setDetectedObjects([]);
  };

  // 이벤트 리스너 추가
  useEffect(() => {
    const handleUserInteraction = () => {
      clearSearchResults();
    };

    // 마우스 클릭, 스크롤, 키보드 입력 시 검색 결과 초기화
    window.addEventListener('click', handleUserInteraction);
    window.addEventListener('scroll', handleUserInteraction);
    window.addEventListener('keydown', handleUserInteraction);

    return () => {
      window.removeEventListener('click', handleUserInteraction);
      window.removeEventListener('scroll', handleUserInteraction);
      window.removeEventListener('keydown', handleUserInteraction);
    };
  }, []);

  // 검색 입력과 버튼 클릭은 검색 결과를 유지하도록 수정
  const handleSearchInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.stopPropagation();  // 이벤트 버블링 방지
    setSearchTerm(e.target.value);
  };

  const handleSearchButtonClick = (e: React.MouseEvent, mode: 'normal' | 'smart') => {
    e.stopPropagation();  // 이벤트 버블링 방지
    handleSearch(mode);
  };

  return (
    <div className="App">
      <h1>찾기</h1>

      <div className="search-section">
        <div className="search-container">
          <input
            type="text"
            placeholder="검색어를 입력하세요..."
            value={searchTerm}
            onChange={handleSearchInputChange}
            onKeyPress={(e) => {
              e.stopPropagation();  // 이벤트 버블링 방지
              if (e.key === 'Enter') {
                handleSearch('normal');
              }
            }}
            className="search-input"
          />
          <div className="search-buttons">
            <button 
              onClick={(e) => handleSearchButtonClick(e, 'normal')}
              className="search-button"
            >
              검색
            </button>
            <button 
              onClick={(e) => handleSearchButtonClick(e, 'smart')}
              className="search-button smart"
            >
              스마트 검색
            </button>
          </div>
        </div>
      </div>

      <div className="upload-section">
        <div className="upload-options">
          <div className="upload-option">
            <h3>이미지 업로드</h3>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileUpload}
              className="file-input"
            />
          </div>
          <div className="upload-option">
            <h3>비디오 업로드</h3>
            <input
              type="file"
              accept="video/*"
              onChange={handleFileUpload}
              className="file-input"
            />
          </div>
        </div>
        {isProcessing && <p>Processing...</p>}
        {error && <p className="error">{error}</p>}
      </div>

      {selectedMedia && (
        <div className="selected-media">
          <div style={{ position: 'relative' }}>
            <img 
              src={selectedMedia.url} 
              alt="Selected" 
              style={{ maxWidth: '100%', maxHeight: '400px' }}
              ref={imageRef}
              onClick={handleImageClick}
            />
            {detectedObjects.map((obj, index) => {
              const img = imageRef.current;
              if (!img) return null;

              const rect = img.getBoundingClientRect();
              const scaleX = rect.width / img.naturalWidth;
              const scaleY = rect.height / img.naturalHeight;

              // 더 정밀한 크기 조정
              const adjustBox = (box: CharBox) => {
                const height = box.y2 - box.y1;
                const width = box.x2 - box.x1;

                return {
                  x1: box.x1 + (width * 0.15),    // 왼쪽 15% 줄임
                  y1: box.y1 + (height * 0.4),    // 위에서 40% 줄임
                  x2: box.x2 - (width * 0.15),    // 오른쪽 15% 줄임
                  y2: box.y2 - (height * 0.4)     // 아래에서 40% 줄임
                };
              };

              if (obj.char_boxes && obj.char_boxes.length > 0) {
                return obj.char_boxes.map((box, charIndex) => {
                  const adjustedBox = adjustBox(box);
                  return (
                    <div
                      key={`${index}-${charIndex}`}
                      style={{
                        position: 'absolute',
                        left: `${adjustedBox.x1 * scaleX}px`,
                        top: `${adjustedBox.y1 * scaleY}px`,
                        width: `${(adjustedBox.x2 - adjustedBox.x1) * scaleX}px`,
                        height: `${(adjustedBox.y2 - adjustedBox.y1) * scaleY}px`,
                        backgroundColor: 'rgba(255, 255, 0, 0.25)',
                        mixBlendMode: 'multiply',
                        pointerEvents: 'none',
                        zIndex: 1
                      }}
                    />
                  );
                });
              } else {
                const adjustedBox = adjustBox(obj.bbox);
                return (
                  <div
                    key={index}
                    style={{
                      position: 'absolute',
                      left: `${adjustedBox.x1 * scaleX}px`,
                      top: `${adjustedBox.y1 * scaleY}px`,
                      width: `${(adjustedBox.x2 - adjustedBox.x1) * scaleX}px`,
                      height: `${(adjustedBox.y2 - adjustedBox.y1) * scaleY}px`,
                      backgroundColor: 'rgba(255, 255, 0, 0.25)',
                      mixBlendMode: 'multiply',
                      pointerEvents: 'none',
                      zIndex: 1
                    }}
                  />
                );
              }
            })}
          </div>
        </div>
      )}

      {mediaItems.length > 0 && (
        <div className="media-grid">
          {mediaItems.map(media => (
            <div
              key={media.id}
              className={`media-item ${selectedMedia?.id === media.id ? 'selected' : ''}`}
              onClick={() => handleMediaClick(media)}
            >
              {media.type === 'video' ? (
                <video src={media.url} />
              ) : (
                <img src={media.url} alt="Uploaded" />
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const formatTime = (seconds: number): string => {
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = Math.floor(seconds % 60);
  return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
};

export default App;
