import React, { useState, useEffect, useRef } from 'react';
import './App.css';

interface CharBox {
  x1: number;
  y1: number;
  x2: number;
  y2: number;
}

interface DetectedObject {
  text: string;
  bbox: CharBox;
}

interface MediaItem {
  id: string;
  type: 'video' | 'image';
  url: string;
  file: File;
}

interface TimelineItem {
  timestamp: number;
  texts: DetectedObject[];
}

interface ApiResponse {
  type: 'image' | 'video';
  objects?: DetectedObject[];
  ocr_text?: string;
  file_url?: string;
  timeline?: TimelineItem[];
}

const App: React.FC = () => {
  const [mediaItems, setMediaItems] = useState<MediaItem[]>([]);
  const [selectedMedia, setSelectedMedia] = useState<MediaItem | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [detectedObjects, setDetectedObjects] = useState<DetectedObject[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const imageRef = useRef<HTMLImageElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const [mediaType, setMediaType] = useState<'image' | 'video'>('image');
  const [mediaUrl, setMediaUrl] = useState<string>('');
  const [timeline, setTimeline] = useState<TimelineItem[]>([]);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setIsProcessing(true);
      setError(null);
      const url = URL.createObjectURL(file);
      const type = file.type.startsWith('video/') ? 'video' : 'image';

      const newMediaItem: MediaItem = {
        id: Date.now().toString(),
        type,
        url,
        file
      };

      setMediaItems(prev => [...prev, newMediaItem]);
      setSelectedMedia(newMediaItem);
      setIsProcessing(false);
    }
  };

  const handleSearch = async (mode: 'normal' | 'smart') => {
    if (!searchTerm || !selectedMedia) {
      setDetectedObjects([]);
      setTimeline([]);
      return;
    }

    try {
      const formData = new FormData();
      formData.append('image', selectedMedia.file);
      formData.append('query', searchTerm);
      formData.append('mode', mode);

      const response = await fetch('http://localhost:5001/analyze-image', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || '검색 중 오류가 발생했습니다');
      }

      const data: ApiResponse = await response.json();

      if (data.type === 'image') {
        setDetectedObjects(data.objects || []);
        setTimeline([]);
        setMediaType('image');
        setMediaUrl(selectedMedia.url);
      } else {
        setDetectedObjects([]);
        setTimeline(data.timeline || []);
        setMediaType('video');
        // 서버에서 받은 비디오 URL 사용
        setMediaUrl(`http://localhost:5001${data.file_url}`);
      }
    } catch (error) {
      console.error('검색 오류:', error);
      setError(error instanceof Error ? error.message : '검색 중 오류가 발생했습니다');
    }
  };

  const handleSearchInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newSearchTerm = e.target.value;
    setSearchTerm(newSearchTerm);

    // 검색어를 완전히 지웠을 때만 하이라이트 제거
    if (newSearchTerm === '') {
      setDetectedObjects([]);
    }
  };

  return (
    <div className="App">
      <h1>찾기</h1>

      <div className="search-section">
        <div className="search-container">
          <input
            type="text"
            placeholder="검색어를 입력하세요..."
            value={searchTerm}
            onChange={handleSearchInputChange}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleSearch('normal');
              }
            }}
            className="search-input"
          />
          <div className="search-buttons">
            <button 
              onClick={() => handleSearch('normal')}
              className="search-button"
            >
              검색
            </button>
            <button 
              onClick={() => handleSearch('smart')}
              className="search-button smart"
            >
              스마트 검색
            </button>
          </div>
        </div>
      </div>

      <div className="upload-section">
        <div className="upload-options">
          <div className="upload-option">
            <h3>이미지 업로드</h3>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileUpload}
              className="file-input"
            />
          </div>
          <div className="upload-option">
            <h3>동영상 업로드</h3>
            <input
              type="file"
              accept="video/*"
              onChange={handleFileUpload}
              className="file-input"
            />
          </div>
        </div>
        {isProcessing && <p>Processing...</p>}
        {error && <p className="error">{error}</p>}
      </div>

      {selectedMedia && (
        <div className="selected-media">
          <div style={{ position: 'relative' }}>
            {mediaType === 'image' ? (
              <img 
                src={mediaUrl} 
                alt="Selected" 
                style={{ maxWidth: '100%', maxHeight: '400px' }}
                ref={imageRef}
              />
            ) : (
              <video
                ref={videoRef}
                src={mediaUrl}
                controls
                style={{ maxWidth: '100%', maxHeight: '400px' }}
              />
            )}
            {detectedObjects.map((obj, index) => {
              const img = imageRef.current;
              if (!img) return null;

              const rect = img.getBoundingClientRect();
              const scaleX = rect.width / img.naturalWidth;
              const scaleY = rect.height / img.naturalHeight;

              return (
                <div
                  key={index}
                  style={{
                    position: 'absolute',
                    left: `${obj.bbox.x1 * scaleX}px`,
                    top: `${obj.bbox.y1 * scaleY}px`,
                    width: `${(obj.bbox.x2 - obj.bbox.x1) * scaleX}px`,
                    height: `${(obj.bbox.y2 - obj.bbox.y1) * scaleY}px`,
                    backgroundColor: 'rgba(255, 255, 0, 0.5)',
                    borderBottom: '2px solid rgba(255, 200, 0, 0.8)',
                    mixBlendMode: 'multiply',
                    pointerEvents: 'none',
                    zIndex: 1,
                    boxShadow: 'inset 0 0 2px rgba(255, 255, 0, 0.8)',
                    outline: '1px solid rgba(255, 255, 0, 0.3)'
                  }}
                />
              );
            })}
          </div>

          {timeline.length > 0 && (
            <div className="timeline-container">
              <h3>타임라인</h3>
              <div className="timeline">
                {timeline.map((item, index) => (
                  <div 
                    key={index} 
                    className="timeline-item"
                    onClick={() => seekToTimestamp(item.timestamp)}
                  >
                    <span className="timestamp">
                      {Math.floor(item.timestamp / 60)}:{Math.floor(item.timestamp % 60).toString().padStart(2, '0')}
                    </span>
                    <div className="texts">
                      {item.texts.map((text, i) => (
                        <div key={i} className="detected-text">
                          {text.text}
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {mediaItems.length > 0 && (
        <div className="media-grid">
          {mediaItems.map(media => (
            <div
              key={media.id}
              className={`media-item ${selectedMedia?.id === media.id ? 'selected' : ''}`}
              onClick={() => {
                setSelectedMedia(media);
                setDetectedObjects([]);
              }}
            >
              <img src={media.url} alt="Uploaded" />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
