import React, { useState, useEffect, useRef } from 'react';
import VideoPlayer from './components/VideoPlayer';
import './App.css';

interface Subtitle {
  text: string;
  startTime: number;
  endTime: number;
}

interface TextBox {
  text: string;
  confidence: number;
  bbox: {
    x1: number;
    y1: number;
    x2: number;
    y2: number;
  };
}

interface MediaItem {
  id: string;
  type: 'video' | 'image';
  url: string;
  file: File;
  textBoxes?: TextBox[];
  subtitles?: Subtitle[];
}

interface DetectedObject {
  label: string;
  bbox: {
    x1: number;
    y1: number;
    x2: number;
    y2: number;
  };
  confidence?: number;
}

const App: React.FC = () => {
  const [mediaItems, setMediaItems] = useState<MediaItem[]>([]);
  const [selectedMedia, setSelectedMedia] = useState<MediaItem | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<Subtitle[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const imageRef = useRef<HTMLImageElement>(null);
  const [detectedObjects, setDetectedObjects] = useState<DetectedObject[]>([]);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setIsProcessing(true);
      setError(null);
      const url = URL.createObjectURL(file);
      const type = file.type.startsWith('video/') ? 'video' : 'image';

      const formData = new FormData();
      formData.append(type, file);

      try {
        const response = await fetch(`http://localhost:5001/upload${type === 'image' ? '-image' : ''}`, {
          method: 'POST',
          body: formData,
        });

        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.error || 'Upload failed');
        }

        const newMediaItem: MediaItem = {
          id: Date.now().toString(),
          type,
          url,
          file,
          textBoxes: data.textBoxes,
          subtitles: data.subtitles
        };

        setMediaItems(prev => [...prev, newMediaItem]);
        setSelectedMedia(newMediaItem);
      } catch (error) {
        console.error('Error uploading file:', error);
        setError(error instanceof Error ? error.message : 'Error uploading file. Please try again.');
      } finally {
        setIsProcessing(false);
      }
    }
  };

  const handleSearch = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const term = event.target.value;
    console.log('검색어:', term);
    setSearchTerm(term);

    if (!term || term.trim() === '') {
      console.log('검색 결과와 형광펜 제거');
      setSearchResults([]);
      setDetectedObjects([]);
      return;
    }

    if (!selectedMedia) {
      console.log('선택된 미디어가 없습니다');
      return;
    }

    console.log('선택된 미디어 타입:', selectedMedia.type);

    if (selectedMedia.type === 'video') {
      console.log('비디오 검색 중...');
      if (selectedMedia.subtitles) {
        const results = selectedMedia.subtitles.filter(subtitle => 
          subtitle.text.toLowerCase().includes(term.toLowerCase())
        );
        console.log('비디오 검색 결과:', results);
        setSearchResults(results);
      } else {
        console.log('자막이 없습니다');
      }
    } else if (selectedMedia.type === 'image') {
      try {
        console.log('이미지 분석 시작...');
        console.log('선택된 미디어 파일:', selectedMedia.file);

        const formData = new FormData();
        formData.append('image', selectedMedia.file);
        formData.append('query', term);

        console.log('이미지 분석 요청 전송...');
        const response = await fetch('http://localhost:5001/analyze-image', {
          method: 'POST',
          body: formData,
        });

        console.log('응답 상태:', response.status);
        const data = await response.json();
        console.log('받은 응답:', data);

        if (!response.ok) {
          throw new Error(data.error || '분석 실패');
        }

        console.log('감지된 객체 설정:', data.objects);
        setDetectedObjects(data.objects || []);
      } catch (error) {
        console.error('이미지 분석 오류:', error);
        setError(error instanceof Error ? error.message : '이미지 분석 중 오류가 발생했습니다');
      }
    }
  };

  const handleMediaClick = (media: MediaItem) => {
    setSelectedMedia(media);
    setSearchTerm('');
    setSearchResults([]);
  };

  const handleTimestampClick = (timestamp: number) => {
    const video = document.querySelector('video');
    if (video) {
      video.currentTime = timestamp;
      video.play();
    }
  };

  const handleImageClick = (event: React.MouseEvent<HTMLImageElement>) => {
    if (!imageRef.current || !selectedMedia?.textBoxes) return;

    const rect = imageRef.current.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    const clickedTextBox = selectedMedia.textBoxes.find(box => {
      const scaleX = rect.width / imageRef.current!.naturalWidth;
      const scaleY = rect.height / imageRef.current!.naturalHeight;

      const scaledX1 = box.bbox.x1 * scaleX;
      const scaledY1 = box.bbox.y1 * scaleY;
      const scaledX2 = box.bbox.x2 * scaleX;
      const scaledY2 = box.bbox.y2 * scaleY;

      return x >= scaledX1 && x <= scaledX2 && y >= scaledY1 && y <= scaledY2;
    });

    if (clickedTextBox) {
      setSearchTerm(clickedTextBox.text);
    }
  };

  return (
    <div className="App">
      <h1>찾기</h1>

      <div className="search-section">
        <input
          type="text"
          placeholder="검색..."
          value={searchTerm}
          onChange={handleSearch}
          className="search-input"
        />
      </div>

      <div className="upload-section">
        <div className="upload-options">
          <div className="upload-option">
            <h3>이미지 업로드</h3>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileUpload}
              className="file-input"
            />
          </div>
          <div className="upload-option">
            <h3>비디오 업로드</h3>
            <input
              type="file"
              accept="video/*"
              onChange={handleFileUpload}
              className="file-input"
            />
          </div>
        </div>
        {isProcessing && <p>Processing...</p>}
        {error && <p className="error">{error}</p>}
      </div>

      {selectedMedia && (
        <div className="selected-media">
          {selectedMedia.type === 'video' ? (
            <video 
              src={selectedMedia.url} 
              controls 
              style={{ maxWidth: '100%', maxHeight: '400px' }}
            />
          ) : (
            <div style={{ position: 'relative' }}>
              <img 
                src={selectedMedia.url} 
                alt="Selected" 
                style={{ maxWidth: '100%', maxHeight: '400px' }}
                onLoad={(e) => {
                  const img = e.target as HTMLImageElement;
                  console.log('Image loaded with size:', img.naturalWidth, 'x', img.naturalHeight);
                }}
              />
              {detectedObjects.map((obj, index) => {
                const img = document.querySelector('.selected-media img') as HTMLImageElement;
                if (!img) return null;

                const rect = img.getBoundingClientRect();
                const scaleX = rect.width / img.naturalWidth;
                const scaleY = rect.height / img.naturalHeight;

                // 신뢰도에 따라 색상 결정
                const confidence = obj.confidence || 0;
                const highlightColor = confidence > 0.8 ? 'rgba(0, 255, 0, 0.3)' : 'rgba(255, 255, 0, 0.3)';
                const borderColor = confidence > 0.8 ? 'green' : 'yellow';
                const labelBgColor = confidence > 0.8 ? 'green' : 'yellow';

                console.log(`Drawing box for: ${obj.label}`, {
                  original: obj.bbox,
                  scaled: {
                    x1: obj.bbox.x1 * scaleX,
                    y1: obj.bbox.y1 * scaleY,
                    x2: obj.bbox.x2 * scaleX,
                    y2: obj.bbox.y2 * scaleY
                  },
                  confidence: confidence
                });

                return (
                  <div
                    key={index}
                    style={{
                      position: 'absolute',
                      left: `${obj.bbox.x1 * scaleX}px`,
                      top: `${obj.bbox.y1 * scaleY}px`,
                      width: `${(obj.bbox.x2 - obj.bbox.x1) * scaleX}px`,
                      height: `${(obj.bbox.y2 - obj.bbox.y1) * scaleY}px`,
                      border: `2px solid ${borderColor}`,
                      backgroundColor: highlightColor,
                      pointerEvents: 'none',
                      zIndex: 1
                    }}
                  >
                    <span style={{
                      position: 'absolute',
                      top: '-20px',
                      left: '0',
                      backgroundColor: labelBgColor,
                      color: 'white',
                      padding: '2px 5px',
                      fontSize: '12px',
                      borderRadius: '3px'
                    }}>
                      {obj.label}
                    </span>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      )}

      {mediaItems.length > 0 && (
        <div className="media-grid">
          {mediaItems.map(media => (
            <div
              key={media.id}
              className={`media-item ${selectedMedia?.id === media.id ? 'selected' : ''}`}
              onClick={() => handleMediaClick(media)}
            >
              {media.type === 'video' ? (
                <video src={media.url} />
              ) : (
                <img src={media.url} alt="Uploaded" />
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const formatTime = (seconds: number): string => {
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = Math.floor(seconds % 60);
  return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
};

export default App;
